function checkButton(e){
    var elem = getElementAt(e.getX(), e.getY());
    if(elem.getColor() == Color.white || elem.getColor() == Color.black && elem != null){
        if(CATSPEED < 10){
            if(pineapples >= priceOfSpeed){
            CATSPEED = CATSPEED + 0.1;
            if(CATSPEED >= 10){
                CATSPEED = 10;
            }
            for(var i = 0; i < priceOfSpeed; i++){
                pineapples--;
            }
            println("CATSPEED: " + CATSPEED);
        }
        }else{
            //println("MAX SPEED");
            var maxSpeed = new Text("MAX SPEED", "9pt Arial");
            maxSpeed.setPosition(upgradeButtonOne.getX()-5, upgradeButtonOne.getY()+36);
            maxSpeed.setColor(Color.black);
            add(maxSpeed);
        }
    }
    if(elem.getColor() == Color.blue || elem.getColor() == Color.green && elem != null){
        if(CATFALL > 1){
            if(pineapples >= priceOfFeather){
            CATFALL = CATFALL - 0.1;
            if(CATFALL <= 1){
                CATFALL = 1;
            }
            for(var i = 0; i < priceOfFeather; i++){
                pineapples--;
            }
            println("CATFALL: " + CATFALL);
        }
        }else{
            //println("MAX FEATHER");
            var maxFeather = new Text("MAX FEATHER", "9pt Arial");
            maxFeather.setPosition(upgradeButtonTwo.getX()-5, upgradeButtonTwo.getY()+36);
            maxFeather.setColor(Color.black);
            add(maxFeather);
        }
    }
    if(elem.getColor() == Color.red || elem.getColor() == Color.gray && elem != null){
        startGame();
    }
}
function time(){
    pineapppleCoins();
    //safetyWater();
    checkCatPos();
    movePlatforms();
    keyDownMethod(move);
    run();
}
function safetyWater(){
    water.move(1, 0);
    if(water.getX() + water.getWidth()/2 > getWidth()){
        water.setPosition(0,getHeight()-13);
    }
}
function waterAnimation(){
    waterCount++;
    if(waterCount == 1 || waterCount == 2){
        water.setImage(waterOne);
        water.setSize(70,70);
    }else if(waterCount == 3 || waterCount == 4){
        water.setImage(waterTwo);
        water.setSize(70,70);
    }else if(waterCount == 5 || waterCount == 6){
        water.setImage(waterThree);
        water.setSize(70,70);
    }else if(waterCount == 7 || waterCount == 8){
        water.setImage(waterFour);
        water.setSize(70,70);
    }else{
        waterCount = 0;
    }
}
function checkCatPos(){
    if(cat.getX() < 0){
        cat.setPosition(cat.getX()+dx*2, cat.getY());
    }else if(cat.getX() + cat.getWidth()/2 > getWidth()-16){
        cat.setPosition(cat.getX()-dx*2, cat.getY());
    }
    if(cat.getY() + cat.getWidth()/2 > getHeight()-15){
        lose();
    }
}
function lose(){
    stopTimer(time);
    var song = new Audio("http://soundbible.com/grab.php?id=1578&type=mp3");
    song.play();
    song.loop = true;
    setTimer(catsGalore, 200);
}
function move(e){
    if(e.keyCode == Keyboard.LEFT){
        cat.move(-CATSPEED, 0);
        cat.setImage(catsLeft);
        cat.setSize(40, 40);
    }
    if(e.keyCode == Keyboard.RIGHT){
        cat.move(CATSPEED, 0);
        cat.setImage(catsRight);
        cat.setSize(40, 40);
    }
}
