function movePlatforms(){
    var rnd = Randomizer.nextInt(0, getWidth()/2);
    platDesOne.move(0, -SPEED);
    platDesTwo.move(0, -SPEED);
    platDesThree.move(0, -SPEED);
    if(platDesOne.getY() <= 0){
        platDesOne.setPosition(rnd, getHeight());
    }
    if(platDesTwo.getY() <= 0){
        platDesTwo.setPosition(rnd, getHeight());
    }
    if(platDesThree.getY() <= 0){
        platDesThree.setPosition(rnd, getHeight());
    }
}
